/**
 * 借助hashmap-key来辅助防止重复
 * @param {number[]} nums 
 */
var getCode = function (nums) {
  return nums.sort().join('-')
}
var findTarget = function (nums, target, index, results) {
  let start = 0
  let end = nums.length - 1

  while (end > start) {
    if (nums[start] + nums[end] > target) {
      end -= 1
    } else if (nums[start] + nums[end] < target) {
      start += 1
    } else {
      if (end !== index && start !== index) {
        let result = [nums[start], nums[end], target? -target:target]
        results[getCode(result)] = result
      }
      end -= 1
      start += 1
    }
  }
}
/**
 * 复杂度比较高。但是想不到更好的办法了。
 * @param {number[]} nums
 * @return {number[][]}
 */
var threeSum = function(nums) {
  if (nums.length < 3) {
    return []
  }
  let len = nums.length
  let results = {}
  nums.sort((a, b) => {
    if (a > b) {
      return 1
    }
    if (a === b) {
      return 0
    }
    if (a < b) {
      return -1
    }
  })
  for (let i = 0; i < len; i++) {
    findTarget(nums, 0 - nums[i], i, results)
  }
  return Object.values(results)
};

